import * as React from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import NotificationsActiveRoundedIcon from "@mui/icons-material/NotificationsActiveRounded";
import AccountCircleRoundedIcon from "@mui/icons-material/AccountCircleRounded";
import StarRoundedIcon from "@mui/icons-material/StarRounded";
import RouteRoundedIcon from "@mui/icons-material/RouteRounded";
import CommentRoundedIcon from "@mui/icons-material/CommentRounded";

const Sidebar = () => {
    const [value, setValue] = React.useState("one");
    const [userName, setUserName] = React.useState("");
    const [projectName, setProjectName] = React.useState("");

    React.useEffect(() => {
        // API 호출을 통해 사용자 이름과 프로젝트 이름 가져오기
        // 예시: fetchUserAndProjectData()
        // fetchUserAndProjectData()
        //   .then((data) => {
        //     setUserName(data.userName);
        //     setProjectName(data.projectName);
        //   })
        //   .catch((error) => {
        //     console.error("Error fetching user and project data:", error);
        //   });

        // 임시 데이터 예시
        const data = {
            userName: "John Doe",
            projectName: "Sample Project",
        };
        setUserName(data.userName);
        setProjectName(data.projectName);
    }, []);

    const handleChange = (event: React.SyntheticEvent, newValue: string) => {
        setValue(newValue);
    };

    return (
        <Box sx={{ display: "flex" }}>
            <Box sx={{ display: "flex", flexDirection: "column" }}>
                <Tabs
                    orientation="horizontal"
                    variant="scrollable"
                    value={value}
                    onChange={handleChange}
                    aria-label="sidebar tabs"
                    sx={{ marginBottom: "16px" }}
                >
                    <Tab value="one" icon={<NotificationsActiveRoundedIcon />} sx={{ minWidth: "7px" }} />
                    <Tab value="two" icon={<AccountCircleRoundedIcon />} sx={{ minWidth: "7px" }} />
                    <Tab value="three" icon={<StarRoundedIcon />} sx={{ minWidth: "7px" }} />
                    <Tab value="four" icon={<RouteRoundedIcon />} sx={{ minWidth: "7px" }} />
                    <Tab value="five" icon={<CommentRoundedIcon />} sx={{ minWidth: "7px" }} />
                </Tabs>
                <Box sx={{ flexGrow: 1, padding: "16px" }}>
                    {value === "one" && (
                        <div>
                            <AccountCircleRoundedIcon />
                            {userName} invited you to {projectName}
                            <div>
                                <button>Accept</button>
                                <button>Decline</button>
                            </div>
                        </div>
                    )}
                    {value === "two" && <div>Tab 2 Content</div>}
                    {value === "three" && <div>Tab 3 Content</div>}
                    {value === "four" && <div>Tab 4 Content</div>}
                    {value === "five" && <div>Tab 5 Content</div>}
                </Box>
            </Box>
        </Box>
    );
};

export default Sidebar;
