import * as React from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import NotificationsActiveRoundedIcon from "@mui/icons-material/NotificationsActiveRounded"; //tab1 alarm
import AccountCircleRoundedIcon from "@mui/icons-material/AccountCircleRounded"; //tab2 member
import StarRoundedIcon from "@mui/icons-material/StarRounded"; //tab3 BookMark&MileStone
import RouteRoundedIcon from "@mui/icons-material/RouteRounded"; //tab4 myTask
import CommentRoundedIcon from "@mui/icons-material/CommentRounded"; //tab5 comment

const Sidebar = () => {
    const [value, setValue] = React.useState("one");

    const handleChange = (event: React.SyntheticEvent, newValue: string) => {
        setValue(newValue);
    };

    return (
        <Box sx={{ display: "flex" }}>
            <Box sx={{ display: "flex", flexDirection: "column" }}>
                <Tabs
                    orientation="horizontal"
                    variant="scrollable"
                    value={value}
                    onChange={handleChange}
                    aria-label="sidebar tabs"
                    sx={{ marginBottom: "16px" }}
                >
                    <Tab value="one" icon={<NotificationsActiveRoundedIcon />} label="Tab 1" sx={{ width: "10%" }} />
                    <Tab value="two" icon={<AccountCircleRoundedIcon />} label="Tab 2" sx={{ width: "10%" }} />
                    <Tab value="three" icon={<StarRoundedIcon />} label="Tab 3" sx={{ width: "5%" }} />
                    <Tab value="four" icon={<RouteRoundedIcon />} label="Tab 4" sx={{ width: "10%" }} />
                    <Tab value="five" icon={<CommentRoundedIcon />} label="Tab 5" sx={{ width: "10%" }} />
                </Tabs>
                <Box sx={{ flexGrow: 1, padding: "16px" }}>
                    {value === "one" && <div>Tab 1 Content</div>}
                    {value === "two" && <div>Tab 2 Content</div>}
                    {value === "three" && <div>Tab 3 Content</div>}
                    {value === "four" && <div>Tab 4 Content</div>}
                    {value === "five" && <div>Tab 5 Content</div>}
                </Box>
            </Box>
        </Box>
    );
};

export default Sidebar;
