import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import UserInformation from "../components/UserInformation";
import ProjectList from "../components/ProjectList";
import AddProject from "./AddProject";
import "../components/Sidebar.css";

type Project = {
    id: number;
    title: string;
    description: string;
};

const MainPage: React.FC = () => {
    const [projects, setProjects] = useState<Project[]>([
        { id: 1, title: "Project 1", description: "Description 1" },
        { id: 2, title: "Project 2", description: "Description 2" },
    ]);

    const handleAddProject = (title: string, description: string) => {
        const newProject = {
            id: Date.now(),
            title,
            description,
        };
        setProjects((prevProjects) => [...prevProjects, newProject]);
    };

    return (
        <div className="main-page">
            <Sidebar>
                <UserInformation
                    profileImage="../images/profile_sample.jpg"
                    username="John Doe11"
                    email="johndoe@example1.com"
                />
                <AddProject onAddProject={handleAddProject} />
            </Sidebar>
            <div className="main-content">{/* Main content goes here */}</div>
        </div>
    );
};

export default MainPage;
